package com.bway.zonghedemo;

import android.os.Bundle;
import android.support.annotation.Nullable;
import android.support.v4.app.Fragment;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ListView;
import android.widget.Toast;

import com.google.gson.Gson;

import org.xutils.common.Callback;
import org.xutils.http.RequestParams;
import org.xutils.view.annotation.ContentView;
import org.xutils.view.annotation.ViewInject;
import org.xutils.x;

import java.util.ArrayList;
import java.util.List;


@ContentView(R.layout.fram)
public class MyFragment extends Fragment {
    @ViewInject(R.id.listview)
    private ListView lv;
    private MyAdapter adapter;
    private List<Data.DataBean.ComicsBean> mlist;
    private String urlpath = "http://api.kkmh.com/v1/daily/comic_lists/0?since=0&gender=0&sa_";

    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        View view = x.view().inject(this, inflater, container);
        initView();
        initData();
        return view;


    }


    private void initView() {
        mlist = new ArrayList<>();
        adapter = new MyAdapter(getActivity(), mlist);
        lv.setAdapter(adapter);
    }

    private void initData() {
        RequestParams params = new RequestParams(urlpath);
        x.http().get(params, new Callback.CommonCallback<String>() {
            @Override
            public void onSuccess(String result) {
                Gson gson = new Gson();
                Data data = gson.fromJson(result, Data.class);
                mlist.addAll(data.getData().getComics());
                adapter.notifyDataSetChanged();
            }

            @Override
            public void onError(Throwable ex, boolean isOnCallback) {
                Toast.makeText(getActivity(), ex.getMessage(), Toast.LENGTH_SHORT).show();
            }

            @Override
            public void onCancelled(CancelledException cex) {

            }

            @Override
            public void onFinished() {

            }
        });
    }

    public static MyFragment getInstance() {
        MyFragment myfragment = new MyFragment();
        return myfragment;
    }
}
